function save_options(){var a=document.getElementById("license").value,b=document.getElementById("stats").checked,c=document.getElementById("ver").checked;chrome.storage.local.set({license:a,stats:b,ver:c},function(a){var b=document.getElementById("status");b.textContent="Options saved.",setTimeout(function(){window.close()},500)})}function restore_options(){chrome.storage.local.get({license:"",stats:!0,ver:!0},function(a){document.getElementById("license").value=a.license,document.getElementById("stats").checked=a.stats,document.getElementById("ver").checked=a.ver})}document.addEventListener("DOMContentLoaded",restore_options),document.getElementById("save").addEventListener("click",save_options);